import React, { useState } from 'react'
import { Register } from './Register'

import { requestLogin } from '../api.js'

export const Login = ({ setAuthToken }) => {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [register, setRegister] = useState(false)
    const [errors, setErrors] = useState('')

    function handleSubmit (e) {
      e.preventDefault()
      requestLogin(username, password)
        .then((data) => {
          if (data && data.data.auth_token) {
            setAuthToken(data.data.auth_token)
          }
        })
        .catch((error) => {
          setErrors(error.message)
        })
    }


    if (register) {
        return (
            <Register setRegister />
        )
    }
    return (
      <>
        {errors &&
          <p>{errors}</p>}
        <h1 className='title'>QuestionBox<button className="logout">Logout</button></h1>
        <div className="login-form">
            <h2 className="login-header">Login</h2>
            <form className='form' onSubmit={handleSubmit}>
                <div className="username">
                    <label className='label'>Username</label>
                    <input
                        className='input'
                        type='text'
                        placeholder='janedoge123'
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div className="password">
                    <label className='label'>Password</label>
                    <input
                        className='input'
                        type='password'
                        // using state to pass a value to this attribute
                        // makes this a controlled component
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>

                <div id='form-buttons'>
                    <button
                        className='login-button'
                        type='submit'
                        >Login
                    </button>
                    <p>New to QuestionBox?</p>
                    <button className='register-button' onClick={() => setRegister(true)}>Register Now!</button>
                </div>
            </form>
        </div>
      </>
  )
}

export default Login
