import React, { useState, useEffect } from "react";
import { QuestionDetails } from "./QuestionDetails";

export const Question = (props) => {
    const [expanded, setExpanded] = useState(false)
    const [selectedQuestion, setSelectedQuestion] = useState(null)

    const { questions } = props

    const seeDetails = (question) => {
        setExpanded(true)
        setSelectedQuestion(question)
    }

    useEffect(() => {
        seeDetails(selectedQuestion)
    }, )


    if (expanded === false && selectedQuestion === null) {
        return (
            <>
                {questions.map((question, idx) => {
                    return (
                        <div className="question-card" onClick={seeDetails(question)}>
                            <h2>{question.title}</h2>
                            <p>{question.username}</p>
                            <p>{question.created_at}</p>
                        </div>
                    )

                })}
            </>
        )
    } else {
        return (
            <QuestionDetails question={selectedQuestion} setExpanded={setExpanded} />
        )
    }
}
